package seok.yun.na.dtos;

import java.util.Date;


public class NoticeDto{
	
	private int ntc_seq;
	private String mem_id ;
	private Date regdate;
	private String title  ;
	private String content;
	private int count  ;
	
	public NoticeDto() {
		super();
		// TODO Auto-generated constructor stub
	}

	public NoticeDto(int ntc_seq, String mem_id, Date regdate, String title, String content, int count) {
		super();
		this.ntc_seq = ntc_seq;
		this.mem_id = mem_id;
		this.regdate = regdate;
		this.title = title;
		this.content = content;
		this.count = count;
	}

	@Override
	public String toString() {
		return "NoticeDto [ntc_seq=" + ntc_seq + ", mem_id=" + mem_id + ", regdate=" + regdate + ", title=" + title
				+ ", content=" + content + ", count=" + count + "]";
	}

	public int getNtc_seq() {
		return ntc_seq;
	}

	public void setNtc_seq(int ntc_seq) {
		this.ntc_seq = ntc_seq;
	}

	public String getMem_id() {
		return mem_id;
	}

	public void setMem_id(String mem_id) {
		this.mem_id = mem_id;
	}

	public Date getRegdate() {
		return regdate;
	}

	public void setRegdate(Date regdate) {
		this.regdate = regdate;
	}

	public String getTitle() {
		return title;
	}

	public void setTitle(String title) {
		this.title = title;
	}

	public String getContent() {
		return content;
	}

	public void setContent(String content) {
		this.content = content;
	}

	public int getCount() {
		return count;
	}

	public void setCount(int count) {
		this.count = count;
	}

}
